import React from "react";
import "./UserMainScreen.css";
import { Link } from "react-router-dom";
import close from './images/close-sidebar.jpg';
import open from  './images/open-sidebar.jpg';

function UserMainScreen() {
    
    const openMenu = () => {
        document.querySelector(".side-bar").classList.add("open");
        document.side.src = close;
      }
    const closeMenu = () => {
        document.querySelector(".side-bar").classList.remove("open");
        document.side.src = open;
        document.getElementById("side").style.display = "none";
      }
    
    var closed = false;
    function btnClick() {
        if (closed) {
            document.querySelector(".side-bar").classList.remove("close");
            document.side.src = close;
            document.getElementById("side").style.display = "";
            closed = false;
        } else {
            document.querySelector(".side-bar").classList.add("close");
            document.side.src = open;
            document.getElementById("side").style.display = "none";
            closed = true;
        }
    }

    
    return (
        <div class="grid-container">
            <header class="header">
                <div class="title">
                    Connecteam+
                </div>
                <div class="clockin-out">
                    <ul>
                        <li>
                            Clock In
                        </li>
                        <li>
                            Clock Out
                        </li>
                    </ul>
                </div>
                
            </header>
            <div class="container">
        
                <aside class="side-bar" id="side-menu">
                    <div>
                        <button class="sidebar-close-button" onClick={btnClick}>
                            <img src={close} alt="close" class="close-btn" name='side'/>
                        </button>
                    </div>
                    
                    <ul class="side-buttons" id="side">
                        <li>
                            <Link to="/signin" type="button" class="btn" name="button">
                                Home
                            </Link>
                        </li>
                        <li>
                            <Link to="/signin" type="button" class="btn" name="button">
                                My tasks
                            </Link>
                        </li>
                        <li>
                            <Link to="/signin" type="button" class="btn" name="button">
                                Calendar
                            </Link>
                        </li>
                        <li>
                            <Link to="/signin" type="button" class="btn" name="button">
                                Messages
                            </Link>
                        </li>
                        <li>
                            <Link to="/signin" type="button" class="btn" name="button">
                                Payroll
                            </Link>
                        </li>
                    </ul>
                    <button class="logout">
                        Logout
                    </button>
                </aside>
                
                <div class="main-contents">
                    
                    <div class="user-icon">
                        Employee Name
                    </div>
                    <div class="user-info">
                        <ul>
                            <li>Employee ID</li>
                            <li>Department</li>
                            <li>Position</li>
                        </ul>
                    </div>
                </div>
            
            </div>
        </div>
        
    );
}

export default UserMainScreen;
